SET NAMES UTF8;
DROP DATABASE IF EXISTS ds;
CREATE DATABASE ds CHARSET=UTF8;
USE ds;
#tag内容列表
CREATE TABLE ds_tag(
  did TINYINT PRIMARY KEY AUTO_INCREMENT,
  dname VARCHAR(12) COMMENT "tag内容"
);
#书目内容1(除目录、内容)
CREATE TABLE ds_list(
  sid INT PRIMARY KEY AUTO_INCREMENT,
  title VARCHAR(32) COMMENT "书名",
  fAuthor VARCHAR(32) COMMENT "第一作者",
  sAuthor VARCHAR(32) COMMENT "第二作者",
  summary VARCHAR(140) COMMENT "内容摘要(list页)",
  intro VARCHAR(1000) COMMENT "内容简介(详情页)",
  newPrice DECIMAL(10,2) COMMENT "现价",
  oldPrice DECIMAL(10,2) COMMENT "原价",
  imgSrc VARCHAR(80) COMMENT "图片src",
  copyright VARCHAR(32) COMMENT "版权",
  evaluate DECIMAL(3,2) COMMENT "评分",
  tagId TINYINT NOT NULL COMMENT "标签页id",
  wcount DECIMAL(5,2) COMMENT "字数",
  size DECIMAL(5,2) COMMENT "大小",
  keywords VARCHAR(20) COMMENT "关键字",
  bnumber VARCHAR(20) COMMENT "书号",
  publish_time DATE COMMENT "出版日期",
  update_time DATE COMMENT "更新日期",
  FOREIGN KEY (tagId) REFERENCES ds_tag (did)
);
#书目内容2(目录+内容)
CREATE TABLE ds_detail(
  did INT PRIMARY KEY AUTO_INCREMENT,
  catalog_title VARCHAR(32) COMMENT "目录标题",
  catalog_content VARCHAR(400) COMMENT "目录简介",
  catalog_number SMALLINT COMMENT "目录序号",
  content VARCHAR(1000) COMMENT "与目录对应的内容",
  sid INT NOT NULL COMMENT "对应书id",
  FOREIGN KEY (sid) REFERENCES ds_list (sid)
);
#评价
CREATE TABLE ds_comment(
  cid INT PRIMARY KEY AUTO_INCREMENT,
  content VARCHAR(140) COMMENT "评价内容",
  evaluate DECIMAL(2,0) COMMENT "评分",
  time DATETIME COMMENT "评价时间",
  zan INT COMMENT "点赞量",
  sid INT NOT NULL COMMENT "对应书ID",
  FOREIGN KEY (sid) REFERENCES ds_list (sid)
);
#搜索(用户搜索推荐表)
CREATE TABLE ds_user_search(
  sid INT PRIMARY KEY AUTO_INCREMENT,
  bid INT NOT NULL COMMENT "对应书ID",
  title VARCHAR(32) COMMENT "书名",
  FOREIGN KEY (bid) REFERENCES ds_list (sid)
);
#用户列表
CREATE TABLE ds_userlist(
  uid INT PRIMARY KEY AUTO_INCREMENT,
  uname VARCHAR(16) UNIQUE  COMMENT "用户名",
  upwd VARCHAR(128) COMMENT "密码"
);
#用户已读列表
CREATE TABLE ds_user_markRead(
  mid INT PRIMARY KEY AUTO_INCREMENT,
  uid INT COMMENT "用户ID",
  bid INT COMMENT "书籍id",
  isDel boolean DEFAULT 0
);
#用户购买列表
CREATE TABLE ds_user_bought(
  gid INT PRIMARY KEY AUTO_INCREMENT,
  bid INT COMMENT "对应书id",
  uid INT COMMENT "对应用户id"
);
#首页第一部分显示书籍
CREATE TABLE ds_product_index_1(
  iid INT PRIMARY KEY AUTO_INCREMENT,
  bid INT COMMENT "对应书籍ID",
  ord INT UNIQUE COMMENT "排列顺序"
);
#首页第二部分显示书籍
CREATE TABLE ds_product_index_2(
  iid INT PRIMARY KEY AUTO_INCREMENT,
  bid INT COMMENT "对应书籍ID",
  ord INT UNIQUE COMMENT "排列顺序" 
);
